import {useEffect,useState} from "react";
import axios from "axios";
import { useGetUserID } from "../hooks/useGetUserID";
import { useCookies } from "react-cookie";
export const Home = () => {
    const [workouts,setWorkouts] =useState([]);
    const[savedworkouts, setSavedWorkout] = useState([]);
    const[cookies,_]= useCookies(["access_token"]);
    const userID=useGetUserID();
    useEffect(() => {
        const fetchWorkout = async() => {
            try {
              const response=  await axios.get("http://localhost:3001/workouts")

                setWorkouts(response.data);

            } catch (err) {
                console.error(err);
            }
        };


        const fetchSavedWorkout = async() => {
            try {
              const response=  await axios.get(`http://localhost:3001/workouts/savedWorkouts/ids/${userID}`);

                setSavedWorkout(response.data.savedWorkout);

            } catch (err) {
                console.error(err);
            }
        };
        fetchWorkout();
        fetchSavedWorkout();
    },[])

const saveWorkout= async(workoutID)=>{
    try {
        const response=  await axios.put("http://localhost:3001/workouts",{workoutID,userID},{headers:{authorization : cookies.access_token}})

          console.log(response);

      } catch (err) {
          console.error(err);
      }
};
const isWorkoutSaved = (id) => savedworkouts.includes(id);

    return (<div>
        <h1>Workouts</h1>

        <ul>
            {workouts.map((workout)=>(
                <li key = {workout._id}>
                    <div>
                        <h2>{workout.title}</h2>
                        <h3>{workout.description}</h3>
                        </div>
                        <div>
                        <button onClick={()=>saveWorkout(workout._id)}
                        disabled={isWorkoutSaved(workout._id)}>
                           { isWorkoutSaved(workout._id)?"Saved" : "Save"}
                        </button>
                        </div>
                   <img src={workout.imageUrl} alt={workout.tittle}/>
                </li>
            ))}
        </ul>
        </div>
    );
};
