import { useState } from "react";
import "../pages/create-workout.css"
export const Createworkout = () => {
    const[workout,setWorkout]=useState({
        title:"",
        description: "",
        exercises:[{
            exercise:"",
            sets: 0,
            reps: 0,
            weight: 0,
            stepsToPerform: "",}

        ],
        imageUrl: "",
        userOwner: 0
    });
    const handleChange = (event) => {
        const{name,value} = event.target;
        setWorkout({...workout,[name] : value});
    };
    const handleExerciseChange = (index, event) => {
        const { name, value } = event.target;
        const exercises = [...workout.exercises];
        exercises[index][name] = value;
        setWorkout({ ...workout, exercises });
      };
      const addExercise = () => {
        setWorkout({
          ...workout,
          exercises: [
            ...workout.exercises,
            {
              exercise: "",
              sets: 0,
              reps: 0,
              weight: 0,
              stepsToPerform: "",
            },
          ],
        });
      };
    return (<div className="create-workout">
        <h2>Create Workout</h2>
        <form>
            <label htmlFor="title">Name</label>
            <input type="text" id="title" name="name" onChange={handleChange}/>
            <label htmlFor="description">Description</label>
            <textarea id="description" name="description"  onChange={handleChange}  ></textarea>
            <label htmlFor="excercises">Excercises</label>
            {workout.exercises.map((exercise, index) => (
          <div key={index}>
            <label htmlFor={`exercise-${index}`}>Exercise</label>
            <br/>
            <input
              type="text"
              id={`exercise-${index}`}
              name="exercise"
              value={exercise.exercise}
              onChange={(event) => handleExerciseChange(index, event)}
            />
            <label htmlFor={`sets-${index}`}>Sets</label>
            <input
              type="number"
              id={`sets-${index}`}
              name="sets"
              value={exercise.sets}
              onChange={(event) => handleExerciseChange(index, event)}
            />
            <label htmlFor={`reps-${index}`}>Reps</label>
            <input
              type="number"
              id={`reps-${index}`}
              name="reps"
              value={exercise.reps}
              onChange={(event) => handleExerciseChange(index, event)}
            />
            <label htmlFor={`weight-${index}`}>Weight</label>
            <input
              type="number"
              id={`weight-${index}`}
              name="weight"
              value={exercise.weight}
              onChange={(event) => handleExerciseChange(index, event)}
            />
            <label htmlFor={`steps-${index}`}>Steps to Perform</label>
            <input
              type="text"
              id={`steps-${index}`}
              name="stepsToPerform"
              value={exercise.stepsToPerform}
              onChange={(event) => handleExerciseChange(index, event)}
            />
          </div>
        ))}
            <button onClick={addExercise}>Add Excercise</button>
            <label htmlFor="imageUrl">Image Url</label>
            <input type="text" id="imageUrl" name ="imageUrl"  onChange={handleChange}/>

        </form>
    </div>
    );
};
