import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import ReactStars from "react-rating-stars-component";
import { BsFillStarFill } from "react-icons/bs";
import { useGetUserID } from "../hooks/useGetUserID";
import  useGetWorkoutID  from "../hooks/useGetWorkoutID";

export const ViewWorkout = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [workoutID,setWorkoutid] = useState();
    // const page = location.state.page;
    const userID = useGetUserID();
    const [workout,setWorkout] = useState([]);
    const [comment,setComment] = useState("");
    const [star ,setStar] = useState(0);

      const getStoredWorkoutID = async () => {
        const storedWorkoutID =  window.localStorage.getItem("workout_id");
        if (storedWorkoutID) {
          console.log(storedWorkoutID,"from local");
          setWorkoutid(storedWorkoutID);
        }
      };

      const storedWorkoutID = parseInt(window.localStorage.getItem("workout_id"), 10);
if (!isNaN(storedWorkoutID)) {
  console.log(storedWorkoutID, "from local");
  setWorkoutid(storedWorkoutID);
}


      getStoredWorkoutID();


    console.log(workoutID,"hey");
    const handleStars = async (e) => {
        setStar(e);
        alert(`${e}` + " stars given");
        try {
            const response = await axios.put("workouts/rate",{
                workoutID : workoutID,
                userID: userID,
                rating:e

            });
        } catch (err) {
            console.error(err);
        }

    };

      const submitComment = async () => {
        if (comment !== "") {
            try {
              const cmt = {
                workoutID: workoutID,
                userID: userID,
                comment: comment,
              };
              const response = await axios.put(
                "workouts/comment",
                cmt
              );
              const comments = [
                ...workout.comments,
                {
                  user: userID,
                  comment: comment,
                  _id: workoutID,
                },
              ];
              console.log(comments);
              setWorkout({ ...workout, comments });
              console.log(workout);
            } catch (err) {
              console.error(err);
            }
          }
      };

      useEffect(() => {
          axios.get(
              `workouts/get-workout/${workoutID}`
            ).then((res)=>{


            console.log(res.data,"view");
            setWorkout(res.data);
          } );
      }, []);

      //if already rated!!
      useEffect(() => {
        if (workout && workout.ratings) {
          const ratingObject = workout.ratings.find(
            (r) => r.user.toString() === userID
          );
          if (ratingObject) {
            console.log("RatingObj");
            console.log(ratingObject.rating);
            setStar(ratingObject.rating);
            console.log("stars value = ");
            console.log(star);
          }
        }
      }, [workout]);

      return (
        <div>

          <h1>Workout</h1>
          <ul>
            <li key={workout._id}>
              <div>
                <h2>{workout.name}</h2>
                <button

                >
                  Back
                </button>
              </div>
              <div className="discription">
                <h4>Description</h4>
                <p>{workout.description}</p>
              </div>
              <img src={workout.imageUrl} alt={workout.name} />
              <h4>Exercises</h4>
        <ul>
          {
          workout.exercises.map((exercise, idx) => (
            <li key={idx}>
              <h5>{exercise.exercise}</h5>
              <p>{`Sets: ${exercise.sets} Reps: ${exercise.reps} Weight: ${exercise.weight}`}</p>
              <p>{exercise.stepsToPerform}</p>
            </li>
          ))}
        </ul> 


              <h4>Comments</h4>
              {workout.comments &&
                workout.comments.map((comment, idx) => (
                  <li key={idx}>{comment.comment}</li>
                  // <p>
                  //   {idx + 1}. {comment.comment}
                  // </p>
                ))}
              <div>
                <input
                  type="text"
                  onChange={(event, idx) => {
                    setComment(event.target.value);
                  }}
                />
                <button
                  onClick={() => {
                    submitComment();
                  }}
                >
                  Submit
                </button>
                {star === 0 ? (
                  <>
                    <h4>Please Rate</h4>
                    <ReactStars
                      count={5}
                      onChange={(e) => {
                        handleStars(e);
                      }}
                      size={40}
                      activeColor="#ffd700"
                    />
                  </>
                ) : (
                  <>
                    <h3>Rated {star} <BsFillStarFill color="#ffd700"/> </h3>
                  </>
                )}
              </div>
            </li>
          </ul>
        </div>
      );
    };
