import {useEffect,useState} from "react";
import axios from "axios";
import { useGetUserID } from "../hooks/useGetUserID";

export const SavedWorkouts = () => {
    const [savedWorkouts,setSavedWorkouts] =useState([]);


    const userID=useGetUserID();
    useEffect(() => {

      const fetchSavedWorkout = async() => {
            try {
              const response=  await axios.get(`workouts/savedWorkouts/${userID}`);
                console.log(response.data);
                setSavedWorkouts(response.data.savedWorkouts);

            } catch (err) {
                console.error(err);
            }
        };

        fetchSavedWorkout();
    },[])




    return (<div>
        <h1>Saved Workouts</h1>
        <ul>
            {savedWorkouts.map((savedworkout)=>(
                <li key = {savedworkout._id}>
                    <div>
                        <h2>{savedworkout.title}</h2>
                        <h3>{savedworkout.description}</h3>
                        </div>

                   <img src={savedworkout.imageUrl} alt={savedworkout.tittle}/>
                </li>
            ))}
        </ul>
        </div>
    );
};
